def decimal_to_hex(decimal_value):
    # Convert the decimal value to its hexadecimal representation
    hex_value = hex(decimal_value)[2:]
    
    # Add a leading zero for single digit hex values
    if len(hex_value) == 1:
        hex_value = "0" + hex_value
    
    return hex_value

